var link=document.querySelector(".modal-button");var popup=document.querySelector(".modal");var close=popup.querySelector(".modal-close");var form=popup.querySelector("form");var fullname=popup.querySelector("[name=fullname]");var email=popup.querySelector("[name=email]");var isStorageSupport=true;var storage="";try{storage=localStorage.getItem("fullname")}catch(err){isStorageSupport=false}link.addEventListener("click",function(a){a.preventDefault();popup.classList.add("modal-show");if(storage){fullname.value=storage;email.focus()}else{fullname.focus()}});close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){if(!fullname.value||!email.value){a.preventDefault();popup.classList.remove("modal-error");popup.offsetWidth=popup.offsetWidth;popup.classList.add("modal-error")}else{if(isStorageSupport){localStorage.setItem("fullname",fullname.value)}}});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(popup.classList.contains("modal-show")){popup.classList.remove("modal-show");popup.classList.remove("modal-error")}}});
